definition.script:
pipeline {


    agent {
        label 'Voyo_Agent'
    }

    environment {
        GIT_REPO = "https://github.com/Genymobile/scrcpy.git"
        CUSTOM_WS = "/home/voyo/jenkins_jobs"
    }


    stages {

        stage('Change to HOME directory') {
            steps {
                sh 'cd ~'
            }
        }



        stage('List content') {
            steps {
                sh 'pwd'
                sh 'ls -l /home/voyo >> ~/content2.log'
                sh 'cat ~/content2.log'
                sh 'pwd'
            }

        }



        stage('Check Git version') {
            steps {
                sh 'git --version'
            }

        }


    }


}
